CREATE PROCEDURE `add_migration`()
BEGIN
DECLARE v INT DEFAULT 1;
SET v = (SELECT COUNT(*) FROM `migrations` WHERE `id`='20230426074021');
IF v=0 THEN
INSERT INTO `migrations` VALUES ('20230426074021');
